import React, { useState } from "react";
import "./styles.css";

const storyNodes = {
  start: {
    text: "Bạn xuyên không về quá khứ, tỉnh dậy tại thôn nhỏ dưới chân núi.",
    choices: [
      { text: "Khám phá ngôi làng", next: "village" },
      { text: "Tìm đường lên núi", next: "mountain" },
    ],
  },
  village: {
    text: "Bạn gặp một ông lão đang rao giảng đạo lý tu tiên.",
    choices: [
      { text: "Lắng nghe", next: "learn" },
      { text: "Bỏ qua", next: "end" },
    ],
  },
  mountain: {
    text: "Bạn gặp mãnh thú và bị thương nặng. Kết thúc vòng trải nghiệm.",
    choices: [],
  },
  learn: {
    text: "Ông lão truyền cho bạn khẩu quyết luyện khí tầng 1.",
    choices: [],
  },
  end: {
    text: "Bạn sống một đời bình thường, không đụng tới tu tiên.",
    choices: [],
  },
};

function App() {
  const [current, setCurrent] = useState("start");

  const node = storyNodes[current];

  return (
    <div className="game-container">
      <h1>Trả Nghiệm Nhân Sinh</h1>
      <p>{node.text}</p>
      <div className="choices">
        {node.choices.map((choice, index) => (
          <button key={index} onClick={() => setCurrent(choice.next)}>
            {choice.text}
          </button>
        ))}
        {node.choices.length === 0 && (
          <button onClick={() => setCurrent("start")}>Chơi lại</button>
        )}
      </div>
    </div>
  );
}

export default App;
