import React, { useState } from "react";
import "./styles.css";
import { tuviData } from "./data/tuvi_full_detailed.ts";
import { linhCanList } from "./data/linhcan.ts";

const storyNodes = {
  start: {
    text: "Bạn xuyên không về quá khứ, tỉnh dậy tại thôn nhỏ dưới chân núi.",
    choices: [
      { text: "Khám phá ngôi làng", next: "village" },
      { text: "Tìm đường lên núi", next: "mountain" },
    ],
  },
  village: {
    text: "Bạn gặp một ông lão đang rao giảng đạo lý tu tiên.",
    choices: [
      { text: "Lắng nghe", next: "learn" },
      { text: "Bỏ qua", next: "end" },
    ],
  },
  mountain: {
    text: "Bạn gặp mãnh thú và bị thương nặng. Kết thúc vòng trải nghiệm.",
    choices: [],
  },
  learn: {
    text: "Ông lão truyền cho bạn khẩu quyết luyện khí tầng 1.",
    choices: [],
  },
  end: {
    text: "Bạn sống một đời bình thường, không đụng tới tu tiên.",
    choices: [],
  },
};

// Hàm random linh căn theo tỉ lệ
function getRandomLinhCan() {
  const weights = {
    common: 60,
    uncommon: 30,
    legendary: 10,
  };

  const expandedList = linhCanList.flatMap((item) =>
    Array(weights[item.rarity]).fill(item)
  );

  const rand = Math.floor(Math.random() * expandedList.length);
  return expandedList[rand];
}

function App() {
  const [hasCharacter, setHasCharacter] = useState(false);
  const [character, setCharacter] = useState({
    name: "",
    gender: "",
    linhCan: null,
  });

  const [tuViLevel, setTuViLevel] = useState(0);
  const [exp, setExp] = useState(0);
  const [current, setCurrent] = useState("start");

  const currentTuVi = tuviData[tuViLevel];
  const node = storyNodes[current];

  const gainExp = (amount: number) => {
    const next = tuviData[tuViLevel + 1];
    if (!next) return;
    const multiplier = character.linhCan?.multiplier || 1;
    const adjusted = amount * multiplier;
    const newExp = exp + adjusted;
    if (newExp >= next.exp) {
      setTuViLevel(tuViLevel + 1);
      setExp(newExp - next.exp);
    } else {
      setExp(newExp);
    }
  };

  if (!hasCharacter) {
    return (
      <div className="game-container">
        <h1>Tạo Nhân Vật</h1>
        <input
          placeholder="Tên nhân vật"
          value={character.name}
          onChange={(e) =>
            setCharacter({ ...character, name: e.target.value })
          }
        />
        <div>
          <label>
            <input
              type="radio"
              name="gender"
              value="Nam"
              onChange={(e) =>
                setCharacter({ ...character, gender: e.target.value })
              }
            />
            Nam
          </label>
          <label>
            <input
              type="radio"
              name="gender"
              value="Nữ"
              onChange={(e) =>
                setCharacter({ ...character, gender: e.target.value })
              }
            />
            Nữ
          </label>
        </div>
        <button
          onClick={() => {
            if (!character.name || !character.gender) {
              alert("Nhập đủ thông tin!");
              return;
            }
            const randomLC = getRandomLinhCan();
            setCharacter((prev) => ({ ...prev, linhCan: randomLC }));
            setHasCharacter(true);
          }}
        >
          Bắt đầu
        </button>
      </div>
    );
  }

  return (
    <div className="game-container">
      <h1>{character.name} - {character.gender}</h1>
      <p>Linh căn: {character.linhCan?.name} ({character.linhCan?.desc})</p>
      <p>Cảnh giới: {currentTuVi.name} - {currentTuVi.desc}</p>
      <p>Kinh nghiệm: {exp}/{tuviData[tuViLevel + 1]?.exp || "MAX"}</p>

      <p>{node.text}</p>
      <div className="choices">
        {node.choices.map((choice, index) => (
          <button key={index} onClick={() => {
            setCurrent(choice.next);
            gainExp(10); // mỗi lựa chọn tăng 10 exp
          }}>
            {choice.text}
          </button>
        ))}
        {node.choices.length === 0 && (
          <button onClick={() => setCurrent("start")}>Chơi lại</button>
        )}
      </div>
    </div>
  );
}

export default App;
